import React from 'react';
import { Text, initializeIcons } from '@fluentui/react';
import { Card } from '@uifabric/react-cards';
import 'office-ui-fabric-react/dist/css/fabric.css';

const container = {
    display: 'flex',
    justifyContent: 'center',
    margin: '10vh 0',
};

const icon = {
    fontSize: 24,
    padding: 15,
    verticalAlign: 'middle',
    paddingLeft: 0,
    color: '#0078d4'
}

const styles = {
    cardStyles: {
        root: {
            background: 'white',
            padding: 20,
            borderTop: '5px solid #0078d4',
            width: '90%',
            maxWidth: '90%',
            margin: 'auto',
        }
    },
    header: {
        root: {
            fontSize: 20,
            fontWeight: 'bold',
        }
    },
    amount: {
        root: {
            fontSize: 16,
            fontWeight: 'bold',
            color: '#0078d4'
        }
    }
};

const cardsInfo = [
    {
        title: 'Current Balance',
        amount: '$21 837',
        icon: 'Money',
        percentage: '2.3',
    },
    {
        title: 'Current Expanses',
        amount: '$10 927',
        icon: 'PaymentCard',
        percentage: '0.3',
    },
    {
        title: 'Current Income',
        amount: '$15 093',
        icon: 'Savings',
        percentage: '1.3',
    },
]

const Cards = () => {
    initializeIcons();
    return (
        <div style={container}>
            {cardsInfo.map((card) => (
                <div className="s-Grid-col ms-sm3 ms-xl3">
                    <Card styles={styles.cardStyles}>
                        <Card.Section>
                            <Card.Item>
                            <i style={icon} className={`ms-Icon ms-Icon--${card.icon}`} aria-hidden="true"></i>
                                <Text styles={styles.header}>{card.title}</Text>
                            </Card.Item>
                            <Card.Item>
                            <Text styles={styles.amount}>{card.amount}</Text>
                            </Card.Item>
                            <Card.Item>
                            <Text styles={styles.percentage}>
                                {card.percentage} %
                                </Text>
                            </Card.Item>
                        </Card.Section>

                    </Card>
                </div>
            ))}
            
        </div>
    )
}

export default Cards
