import React from "react";
import { Component } from "react";
import FlowContent from "./flowcontent";
import NavBar from "./components/Header_Component/Header_Component";
// import Cards from "./components/Card_Component/Card_Component";
import Navigation from "./components/Navigation_Component/NavigationComponent";
import "office-ui-fabric-react/dist/css/fabric.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      loading: true,
      login: false,
      token: "-",
      data: {},
    };
  }

  updateData(text) {
    this.setState({ data: text });
  }

  /* login to access flows */
  login() {
    const loginData = {
      loginName: "Eline.de.Groot@woonplezier.nu",
      password: "Woonplezier123",
    };
    console.log("login running");
    fetch("https://teambp-test.azurewebsites.net/api/iris/auth", {
      method: "POST",
      headers: { "Content-type": "application/json" },
      body: JSON.stringify(loginData),
    })
      .then((r) => r.json())
      .then((res) => {
        /* Initiate the flow */
        this.initFlow({ token: res.token });
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }

  continueFlow = (data, token, id) => {
    //var data1 = JSON.parse("{ \"" + outputKey + "\" : \"" + val + "\" }");
    console.log("continueFlow  running");
    console.log(data);
    if (data !== "") {
      data = JSON.parse(data);
    }

    fetch(
      "https://teambp-test.azurewebsites.net/api/iris/flow/instance/" +
        id +
        "/continue?debug=true",
      {
        method: "POST",
        headers: {
          Authorization: "Bearer " + token,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      }
    )
      .then((r) => r.json())
      .then((res) => {
        console.log(res);
        this.setState({
          loading: false,
          token: token,
          data: res,
        });
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  continueFlow_old = (outputKey, val, token, id, data) => {
    var data1 = JSON.parse('{ "' + outputKey + '" : "' + val + '" }');
    console.log(data1);

    fetch(
      "https://teambp-test.azurewebsites.net/api/iris/flow/instance/" +
        id +
        "/continue?debug=true",
      {
        method: "POST",
        headers: {
          Authorization: "Bearer " + token,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data1),
      }
    )
      .then((r) => r.json())
      .then((res) => {
        console.log(res);

        this.setState({
          loading: false,
          token: token,
          data: res,
        });
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  componentDidMount() {
    return this.login();
  }

  initFlow(props) {
    fetch(
      "https://teambp-test.azurewebsites.net/api/iris/flow/instance/BeeindigenHuurContract/init",
      {
        method: "POST",
        headers: { Authorization: "Bearer " + props.token },
        body: JSON.stringify(),
      }
    )
      .then((r) => r.json())
      .then((res) => {
        this.setState({
          loading: false,
          token: props.token,
          data: res,
        });
        //this.continueFlow({token:props.token});
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }

  render() {
    //const s = this.state.loading? "loading": "loaded"
    return (
      <div key="main">
        <NavBar />
        <div className="ms-Grid" dir="ltr">
          <div className="ms-Grid-row">
            <div className="ms-Grid-col ms-sm3 ms-xl3">
              <Navigation />
            </div>
            <div className="main-element ms-Grid-col ms-sm9 ms-xl9">
              <div className="ms-Grid-row">{/* <Cards /> */}</div>
              <div className="ms-Grid-row">
                <FlowContent
                  data={this.state.data}
                  token={this.state.token}
                  continueFlow={this.continueFlow}
                  key={this.state.token}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
